function Clients() {
  const [pageNumber, setPageNumber] = useState(0);
  const numberOfResultsOnPage = 10;
  const { apiData } = useQuery({
    url: apiRoute.clients + `?page=${pageNumber}&size=${numberOfResultsOnPage}`,
