import React from "react";

function UpcomingBookingsProfile({ clientDataBookings }) {

  const handleBookingStatus = (bookingStatus) =>{

    console.log(bookingStatus);
  }

  return (
    <div className="timeline timeline-inverse">
      {clientDataBookings.map((item) => (
        <div key={item.id}>
          <i className="fas fa-calendar bg-primary" />
          <div className="timeline-item">
            <span className="time">
              <i className="far fa-clock" /> Created at{" "}
              {new Date(item.createdDate).toUTCString()}
            </span>
            <h3 className="timeline-header">
              {new Date(item.bookingDate).toLocaleDateString("ro-RO")}
            </h3>
            <div className="timeline-body">
              <ul>
                <li>
                  Date:{" "}
                  {new Date(item.bookingDate).toLocaleDateString("ro-RO", {
                    weekday: "long",
                    year: "numeric",
                    month: "long",
                    day: "numeric",
                  })}
                </li>
                <li>
                  Time: {new Date(item.bookingDate).toLocaleTimeString("ro-RO")}
                </li>
                <li>Special request: No special request</li>
              </ul>
            </div>
            <div class="timeline-footer">
              <button onClick={()=>handleBookingStatus()} class="btn btn-primary btn-sm">
                Confirm
              </button>
              <button class="btn btn-danger btn-sm">
                Cancel
              </button>
            </div>
          </div>
        </div>
      ))}
      <div>
        <i className="far fa-clock bg-gray" />
      </div>
    </div>
  );
}

export default UpcomingBookingsProfile;
