export default function formatDate(date, option) {
    const dateToFormat = new Date(date);
    switch (option) {
        case "time":
            return dateToFormat.toLocaleTimeString("ro-RO")
        default:
            return dateToFormat
    }



  return dateToFormat.toLocaleDateString("ro-RO")
}
