import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "../api/axios";
import apiRoute from "../api/apiRoute";
import FormProfile from "./components/FormProfile";
import UpcomingBookingsProfile from "./components/UpcomingBookingsProfile";
import { useHistory } from "react-router-dom";

function ClientProfile() {
  const { id } = useParams();
  const [clientData, setClientData] = useState([]);
  const [clientDataBookings, setClientDataBookings] = useState([]);
  const history = useHistory();

  useEffect(() => {
    async function fetchData() {
      try {
        const dataClientResponse = await axios.get(apiRoute.clients + `/${id}`);
        setClientData(dataClientResponse.data);
        const dataBookingsResponse = await axios.get(
          apiRoute.bookings + `/history/${id}`
        );
        setClientDataBookings(dataBookingsResponse.data);
      } catch (error) {
        console.log(error);
        // error message notification
      }
    }
    fetchData();
  }, [id]);
  console.log(clientDataBookings);

  const handleDeleteClient = () => {
    deleteClient(clientData.clientId).then((res) => {
      if (res.status === 200) {
        history.push("/");
      } else {
        console.log("error");
      }
    });
  };

  return (
    <div className="content-wrapper">
      {/* Content Header (Page header) */}
      <section className="content-header">
        <div className="container-fluid">
          <div className="row mb-2">
            <div className="col-sm-6">
              <h1>Profile</h1>
            </div>
          </div>
        </div>
        {/* /.container-fluid */}
      </section>
      {/* Main content */}
      <section className="content">
        <div className="container-fluid">
          <div className="row">
            <div className="col-md-3">
              {/* Profile Image */}
              <div className="card card-primary card-outline">
                <div className="card-body box-profile">
                  <div className="text-center">
                    <img
                      className="profile-user-img img-fluid img-circle"
                      src="../../dist/img/avatar5.png"
                      alt="Userprofile"
                    />
                  </div>
                  <h3 className="profile-username text-center">
                    {clientData.firstName} - {clientData.lastName}
                  </h3>
                  <ul className="list-group list-group-unbordered mb-3">
                    <li className="list-group-item">
                      <b>E-mail</b> {clientData.email}
                    </li>
                    <li className="list-group-item">
                      <b>Phone</b> {clientData.phoneNo}
                    </li>
                  </ul>
                  <button onClick={handleDeleteClient} className="btn btn-danger btn-block">Delete</button>
                </div>
              </div>
              <div className="card card-primary"></div>
            </div>

            <div className="col-md-9">
              <div className="card">
                <div className="card-header p-2">
                  <ul className="nav nav-pills">
                    <li className="nav-item">
                      <a
                        className="nav-link active"
                        href="#activity"
                        data-toggle="tab"
                      >
                        Bookings
                      </a>
                    </li>
                    <li className="nav-item">
                      <a
                        className="nav-link"
                        href="#timeline"
                        data-toggle="tab"
                      >
                        Upcoming Bookings
                      </a>
                    </li>
                    <li className="nav-item">
                      <a
                        className="nav-link"
                        href="#settings"
                        data-toggle="tab"
                      >
                        Modify Profile
                      </a>
                    </li>
                  </ul>
                </div>
                {/* /.card-header */}
                <div className="card-body">
                  <div className="tab-content">
                    <div className="active tab-pane" id="activity">
                      {/* /.post */}
                      {/* Post */}
                      <div className="post clearfix">
            <table className="table table-sm">
  <thead>
    <tr>
      <th style={{width: 10}}>#</th>
      <th>Task</th>
      <th>Progress</th>
      <th style={{width: 40}}>Label</th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <td>1.</td>
      <td>Update software</td>
      <td>
        <div className="progress progress-xs">
