import React, { useEffect, useState } from "react";
import axios from "../api/axios";
import apiRoute from "../api/apiRoute";

function Bookings() {
  const [bookingsData, setBookingsData] = useState([]);
  const [pageNo, setPageNo] = useState(0);

  useEffect(() => {
    async function fetchData() {
      try {
        const responseData = await axios.get(
          apiRoute.bookings + `?page=${pageNo}`
        );
        setBookingsData(responseData.data);
      } catch (error) {
        console.log(error);
      }
    }
    fetchData();
  }, [pageNo]);

  console.log(bookingsData.length);
  const handleNextPage = () => {
    setPageNo(pageNo + 1);
  };
  return (
    <div className="content-wrapper">
      {/* Content Header (Page header) */}
      <section className="content-header">
        <div className="container-fluid">
          <div className="row mb-2">
            <div className="col-sm-6">
              <h1>Bookings</h1>
            </div>
            <div className="col-sm-6">
              <ol className="breadcrumb float-sm-right">
                <li className="breadcrumb-item">
                  <a href="/search">Add Booking</a>
                </li>
              </ol>
            </div>
          </div>
        </div>
      </section>
      <section className="content">
        <div className="container-fluid">
          <div className="row">
            <div className="col-12">
              <div className="card">
                <div className="card-header">
                  <h3 className="card-title">Bookings</h3>
                </div>
                <div className="card-body">
                  <table
                    id="example2"
                    className="table table-bordered table-hover"
                  >
                    <thead>
                      <tr>
                        <th>Crt</th>
                        <th>Date</th>
                        <th>Time</th>
                        <th>Client</th>

                        <th>Status</th>
                        <th>Created</th>
                        <th>Updated</th>
                      </tr>
                    </thead>
                    <tbody>
                      {bookingsData.map((item, index) => (
                        <tr key={item.id}>
                          <td>{index + 1}</td>
                          <td>
                            {new Date(item.bookingDate).toLocaleDateString(
                              "ro-RO"
                            )}
                          </td>
                          <td>
                            {new Date(item.bookingDate).toLocaleTimeString(
                              "ro-RO"
                            )}
                          </td>
                          <td>
                            <a href={`/clients/${item.client.clientId}`}>
                              {item.client.firstName}
                            </a>
                          </td>
                                                <td>{item.bookingStatus}</td>
                          <td>{new Date(item.createdDate).toUTCString()}</td>
                          <td>{new Date(item.updatedDate).toUTCString()}</td>
                        </tr>
                      ))}
                    </tbody>
                    {bookingsData.length >= 10 && (
                      <tfoot>
                        <tr>
                          <td>
                            <button onClick={handleNextPage}>Next</button>
                          </td>
                        </tr>
                      </tfoot>
                    )}
                  </table>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
}

export default Bookings;
