import React from "react";
import { useForm, Controller } from "react-hook-form";
import axios from "../../api/axios";
import apiRoute from "../../api/apiRoute";
import { useHistory } from "react-router-dom";
import { toast } from "react-toastify";
import PhoneInput from "react-phone-input-2";
import "react-phone-input-2/lib/style.css";

function FormProfileModify({ clientData }) {
  const { register, handleSubmit, control, errors } = useForm();
  const history = useHistory();

  const onSubmit = (formData) => {
    let updateData = {
      clientId: clientData.clientId,
      firstName: formData.firstName,
      lastName: formData.lastName,
      email: formData.email,
      phoneNo: formData.phoneNo,
    };
    console.log(updateData);
    updateClient(updateData)
      .then((res) => {
        // localStorage.setItem(
        //   "message",
        //   `Client profile ID:${res.clientId} updated`
        // );
        // history.go(0);
      })
      .catch((error) => {
        toast.error("Something went wrong! Modify profile");
        history.push({
          pathname: "/error",
          state: { detail: error.message },
        });
      });
  };

  return (
    <form className="form-horizontal" onSubmit={handleSubmit(onSubmit)}>
      <div className="form-group row">
        <label htmlFor="inputFirstName" className="col-sm-2 col-form-label">
          First Name
        </label>
        <div className="col-sm-10">
          <input
            type="text"
            className="form-control"
            id="inputFirstName"
            defaultValue={clientData.firstName}
            placeholder="First Name"
            name="firstName"
            ref={register({
              required: { message: "This field is required", value: true },
              maxLength: {
                message: "This field cannot exceed 20 characters",
                value: 20,
              },
              minLength: {
                message: "This field cannot be less then 3 characters",
                value: 3,
              },
              pattern: {
                message: "Alphabetical characters only",
                value: /^[A-Za-z]+$/i,
              },
            })}
          />
          {errors.firstName && (
            <small className="text-danger">{errors.firstName.message}</small>
          )}
        </div>
      </div>
      <div className="form-group row">
        <label htmlFor="inputLastName" className="col-sm-2 col-form-label">
          Last Name
        </label>
        <div className="col-sm-10">
          <input
            type="text"
            className="form-control"
            id="inputLastName"
            placeholder="Last Name"
            defaultValue={clientData.lastName}
            name="lastName"
            ref={register({
              required: { message: "This field is required", value: true },
              maxLength: {
                message: "This field cannot exceed 20 characters",
                value: 20,
              },
              minLength: {
                message: "This field cannot be less then 3 characters",
                value: 3,
              },
              pattern: {
                message: "Alphabetical characters only",
                value: /^[A-Za-z]+$/i,
              },
            })}
          />
          {errors.lastName && (
            <small className="text-danger">{errors.lastName.message}</small>
          )}
        </div>
      </div>
      <div className="form-group row">
        <label htmlFor="inputEmail" className="col-sm-2 col-form-label">
          Email
        </label>
        <div className="col-sm-10">
          <input
            type="email"
            className="form-control"
            name="email"
            id="inputEmail"
            placeholder="Email"
            defaultValue={clientData.email}
            ref={register({
              required: { message: "This field is required", value: true },
            })}
          />
          {errors.email && (
            <small className="text-danger">{errors.email.message}</small>
          )}
        </div>
      </div>
      <div className="form-group row">
        <label htmlFor="inputPhoneNo" className="col-sm-2 col-form-label">
          Phone
        </label>
        <div className="col-sm-10">
          {/* <input
            type="number"
            className="form-control"
            id="inputPhoneNo"
            placeholder="Phone"
            name="phoneNo"
            defaultValue={clientData.phoneNo}
            ref={register({
              required: { message: "This field is required", value: true },
              minLength: {
                message: "This field cannot be less then 4 digits",
                value: 4,
              },
              maxLength: {
                message: "This field cannot exceed 15 characters",
                value: 15,
              },
            })}
          /> */}
          {errors.phoneNo && (
            <small className="text-danger">{errors.phoneNo.message}</small>
          )}

          <Controller
            name="phoneNo"
            control={control}
            render={({ onChange }) => (
              <PhoneInput
                placeholder="Enter phone number"
                country="ro"
                on
                regions={"europe"}
                onChange={onChange}
              />
            )}
            rules={{
              minLength: {
                message: "This field cannot be less then 10 characters",
                value: 11,
              },
            }}
          />
        </div>
      </div>
      <div className="form-group row">
        <div className="offset-sm-2 col-sm-10">
          <button type="submit" className="btn btn-primary">
            Update
          </button>
        </div>
      </div>
    </form>
  );
}

export default FormProfileModify;

async function updateClient(updateData) {
  const dataResponse = await axios.put(apiRoute.clients, updateData);
  return dataResponse.data;
}
