import React from "react";
import { useForm, Controller } from "react-hook-form";
import { useHistory } from "react-router-dom";
import axios from "../../api/axios";
import apiRoute from "../../api/apiRoute";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

function FormProfileAddBooking({clientId}) {

    const { handleSubmit, control } = useForm();

    const history = useHistory();
  const onSubmit = (data) => {
    let BookingDTO = {
      clientId: clientId,
      bookingDate: data.bookingDate,
    };
    addBooking(BookingDTO).then((res) => {
      if (res.id >= 0) {
        history.push(`/clients/${id}`);
      } else {
        console.log("error");
      }
    });
  };

  return (
    <form className="form-horizontal" onSubmit={handleSubmit(onSubmit)}>
      <div className="form-group row">
        <label htmlFor="inputFirstName" className="col-sm-2 col-form-label">
          First Name
        </label>
        <div className="col-sm-10">
          <input
            type="text"
            className="form-control"
            id="inputFirstName"
            defaultValue={clientData.firstName}
            placeholder="First Name"
            name="firstName"
            ref={register({ required: true })}
          />
        </div>
      </div>
      <div className="form-group row">
        <label htmlFor="inputLastName" className="col-sm-2 col-form-label">
          Last Name
        </label>
        <div className="col-sm-10">
          <input
            type="text"
            className="form-control"
            id="inputLastName"
            placeholder="Last Name"
            defaultValue={clientData.lastName}
            name="lastName"
            ref={register({ required: true })}
          />
        </div>
      </div>
      <div className="form-group row">
        <label htmlFor="inputEmail" className="col-sm-2 col-form-label">
          Email
        </label>
        <div className="col-sm-10">
          <input
            type="email"
            className="form-control"
            name="email"
            id="inputEmail"
            placeholder="Email"
            defaultValue={clientData.email}
            ref={register({ required: true })}
          />
        </div>
      </div>
      <div className="form-group row">
        <label htmlFor="inputPhoneNo" className="col-sm-2 col-form-label">
          Phone
        </label>
        <div className="col-sm-10">
          <input
            type="text"
            className="form-control"
            id="inputPhoneNo"
            placeholder="Phone"
            name="phoneNo"
            defaultValue={clientData.phoneNo}
            ref={register({ required: true })}
          />
        </div>
      </div>
      <div className="form-group row">
        <div className="offset-sm-2 col-sm-10">
          <button type="submit" className="btn btn-primary">
            Update
          </button>
        </div>
      </div>
    </form>
  );
}

export default FormProfileAddBooking;

async function addBooking(data) {
  try {
    let responseData = await axios.post(apiRoute.bookings, data);
    return responseData.data;
  } catch (error) {
    console.log(error);
  }
}
